mapper.select:
SELECT a.cover, a.title, FROM_UNIXTIME( a.publish_time / 1000, '%Y-%m-%d %H:%i:%s' ) AS publish_time, FROM_UNIXTIME( a.create_time / 1000, '%Y-%m-%d %H:%i:%s' ) AS create_time, FROM_UNIXTIME( a.update_time / 1000, '%Y-%m-%d %H:%i:%s' ) AS update_time, FROM_UNIXTIME( a.delete_time / 1000, '%Y-%m-%d %H:%i:%s' ) AS delete_time, a.pageview, a.sub_message, a.id, c.id AS tagId, c.NAME AS tagName, a.cgId, a.cgName FROM ( SELECT a.cover, a.id, a.title, a.publish_time, a.create_time, a.update_time, a.delete_time, a.pageview, a.sub_message, b.NAME AS cgName, b.id AS cgId FROM article a LEFT JOIN category b ON a.category_id = b.id AND b.`status` = 0 WHERE a.`status` = #{status,jdbcType=SMALLINT}
and (title like CONCAT('%',#{searchValue,jdbcType=VARCHAR},'%') or sub_message like CONCAT('%',#{searchValue,jdbcType=VARCHAR},'%') )
) a LEFT JOIN article_tag_mapper b ON a.id = b.article_id LEFT JOIN tag c ON b.tag_id = c.id AND c.STATUS = 0 ORDER BY update_time DESC LIMIT #{page,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.update:
update article
set pageView = pageView+1
where id=#{id,jdbcType=VARCHAR}

mapper.delete:
delete from article
where id=#{id,jdbcType=VARCHAR}
and status=1
