mapper.select:
select 

,

from friends
where aid = #{aid,jdbcType=INTEGER}
  and friend_id = #{friendId,jdbcType=VARCHAR}

mapper.delete:
delete from friends
where aid = #{aid,jdbcType=INTEGER}
  and friend_id = #{friendId,jdbcType=VARCHAR}

mapper.insert:
insert into friends (aid, friend_id, name, 
  create_time, update_time, delete_time, 
  status, type_id, url
  )
values (#{aid,jdbcType=INTEGER}, #{friendId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=INTEGER}, #{updateTime,jdbcType=INTEGER}, #{deleteTime,jdbcType=INTEGER}, 
  #{status,jdbcType=BIT}, #{typeId,jdbcType=INTEGER}, #{url,jdbcType=LONGVARCHAR}
  )

mapper.update:
update friends
set name = #{name,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=INTEGER},
