  delete_time = #{deleteTime,jdbcType=INTEGER},
  status = #{status,jdbcType=BIT},
  type_id = #{typeId,jdbcType=INTEGER},
  url = #{url,jdbcType=LONGVARCHAR}
where aid = #{aid,jdbcType=INTEGER}
  and friend_id = #{friendId,jdbcType=VARCHAR}

